import React from 'react';
import { Field, reduxForm } from 'redux-form';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from "@material-ui/core/Button";

const useStyles = makeStyles(theme => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: 200,
        },
    },
}));

const renderTextField = field => (
    <div className={useStyles.root}>
        <TextField {...field.input}
                   label={field.label}
                   name={field.name}
        />
    </div>
);


const AddNewEmployeeForm = props => {
    const { handleSubmit, pristine, reset, submitting } = props;
    const classes = useStyles();



    function submit(values) {
        console.log(values);
    }

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <Field name="firstName" component={ renderTextField } label="First Name"/>
            </div>
            <div>
                <Field name="lastName" component={renderTextField} label="Last Name"/>
            </div>
            <div>
                <Field name="email" component={renderTextField} label="Email"/>
            </div>
            <div>
                <Button variant="contained" type="submit" disabled={pristine || submitting}>Submit</Button>
                <Button variant="contained" disabled={pristine || submitting} onClick={reset}>Clear Values</Button>
            </div>
        </form>
    )
};

export default reduxForm({
    form: 'addEmployeeForm',  // a unique identifier for this form
})(AddNewEmployeeForm)
