import React, {Component} from 'react';
import MaterialTable from 'material-table';
import { fetchAllEmployees } from './../actions/postsActions';
import {connect} from 'react-redux';

class PostsTable extends Component {

    componentWillMount() {
        const {getAllPosts, allPosts} = this.props;
        getAllPosts();
        console.log(allPosts);
    }

    render() {
        let { isLoading } = this.props;
        let comp = (<MaterialTable
                title="Basic Search Preview"
                columns={[
                    { title: 'Name', field: 'name' },
                    { title: 'Surname', field: 'surname' },
                    { title: 'Birth Year', field: 'birthYear', type: 'numeric' },
                    {
                        title: 'Birth Place',
                        field: 'birthCity',
                        lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
                    },
                ]}
                data={[
                    { name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 },
                    { name: 'Zerya Betül', surname: 'Baran', birthYear: 2017, birthCity: 34 },
                ]}
                options={{
                    search: true,
                    rowStyle: {
                        backgroundColor: '#EEE',
                    },
                }}
            />);

        return (
            <div>
            {
                isLoading ? <h1>Loading...</h1> : comp
            }
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        isLoading: state.postReducer.isFetching,
        allPosts: state.postReducer.posts
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        getAllPosts: () => dispatch(fetchAllEmployees())
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(PostsTable);
