import React from "react";
import { Field, FieldArray, reduxForm } from "redux-form";
import FirstNameField from "../components/FirstNameField";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

const renderField = ({ input, label, type, meta: { touched, error } }) => (
    <div>
        <label>{label}</label>
        <div>
            {/*<input {...input} type={type} placeholder={label} />*/}
            <TextField {...input}
                required
                id="standard-required"
                label="First Name"
                onChange={(event) => firstName(event.target.value)} />
            {touched && error && <span>{error}</span>}
        </div>
    </div>
);

const renderMembers = ({ fields, meta: { error, submitFailed } }) => (
    <ul>
        <li>
            <button type="button" onClick={() => fields.push({})}>
                Add Member
            </button>
            {submitFailed && error && <span>{error}</span>}
        </li>
        {fields.map((member, index) => (
            <li key={index}>
                <button type="button" onClick={() => fields.remove(index)}>
                    Remove Member
                </button>
                <h4>Member #{index + 1}</h4>
                <Field
                    name={`${member}.firstName`}
                    type="text"
                    component={renderField}
                    label="First Name"
                />
                <Field
                    name={`${member}.lastName`}
                    type="text"
                    component={renderField}
                    label="Last Name"
                />
            </li>
        ))}
    </ul>
);


const AddNewEmployeeFormFunc = ({ handleSubmit, pristine, reset, submitting }) => {

    let[firstName, setFirstName] = React.useState('');

    let formSubmit = (values) => {
        console.log(values);
    };

    const renderField = (field) => {
        return (
            <FirstNameField firstName={(val) => setFirstName(val)} />
        );
    };

    return (
        <form onSubmit={ handleSubmit(formSubmit) }>
            <FieldArray name="members" component={renderMembers} />
            <button type="submit" disabled={submitting}>Add</button>
            <Button variant="contained" color="primary" disabled={pristine || submitting} onClick={reset}>Clear Values</Button>
        </form>
    );
};

const AddNewEmployeeForm = reduxForm({
   form: 'addEmployeeForm'
})(AddNewEmployeeFormFunc);

export default AddNewEmployeeForm;
