import React, {useEffect} from 'react';
import {connect} from 'react-redux';
import {fetchAllEmployees} from '../actions/employeeActions';
import MUIDataTable from "mui-datatables";

const EmployeesList = (props) => {

    useEffect(() => {
        props.fetchEmployees();
    }, [props.employees]);

    const data = props.employees;
    const columns = [
        {
            name: "employeeId",
            label: "Id",
            options: {
                filter: true,
                sort: true,
            }
        },
        {
            name: "firstName",
            label: "First Name",
            options: {
                filter: true,
                sort: true,
            }
        },
        {
            name: "lastName",
            label: "Last Name",
            options: {
                filter: true,
                sort: true,
            }
        },
        {
            name: "email",
            label: "Email",
            options: {
                filter: true,
                sort: false,
            }
        },
        {
            name: "phoneNumber",
            label: "Phone",
            options: {
                filter: true,
                sort: false,
            }
        },
        {
            name: "hireDate",
            label: "Hire Date",
            options: {
                filter: true,
                sort: true,
            }
        },
        {
            name: "jobId",
            label: "Job Id",
            options: {
                filter: true,
                sort: false,
            }
        },
        {
            name: "salary",
            label: "Salary",
            options: {
                filter: true,
                sort: true,
            }
        },
        {
            name: "commissionPct",
            label: "Commission",
            options: {
                filter: true,
                sort: true,
            }
        },
        {
            name: "managerId",
            label: "Manager Id",
            options: {
                filter: true,
                sort: false,
            }
        },
        {
            name: "departmentId",
            label: "Department Id",
            options: {
                filter: true,
                sort: false,
            }
        },
    ];

    const options = {
        filterType: 'checkbox',
        selectableRows: 'none',
    };

    return (
        <MUIDataTable
            title={"Employee List"}
            data={data}
            columns={columns}
            options={options}
        />
    );
};

const mapStateToProps = (state) => {
    return {
        employees: state.empReducer.employees,
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        fetchEmployees: () => dispatch(fetchAllEmployees()),
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(EmployeesList);
