import React from 'react';
import { Field, reduxForm } from 'redux-form';
import Button from "@material-ui/core/Button";
import FirstNameField from "../components/FirstNameField";
import LastNameField from "../components/LastNameField";

const onFormSubmit = (values) => {
    console.log(values);
};

const AddNewEmployeeForm = props => {
    const { handleSubmit, pristine, reset, submitting } = props;

    let[firstName, setFirstName] = React.useState('');
    let[lastName, setLastName] = React.useState('');

    function onFirstNameChange(event) {
        setFirstName(event.target.value);
    }

    function onLastNameChange(event) {
        setLastName(event.target.value);
    }

    return (
        <form onSubmit={handleSubmit(onFormSubmit)}>
            <div>
                <Field
                    name="firstName"
                    label="First Name"
                    onChange={(event) => onFirstNameChange(event)}
                    component={FirstNameField} />
            </div>
            <div>
                <Field
                    name="lastName"
                    label="Last Name"
                    onChange={(event) => onLastNameChange(event)}
                    component={LastNameField} />
            </div>
            <div>
                <Button variant="contained" color="primary" type="submit" disabled={pristine || submitting}>Submit</Button>
                <Button variant="contained" color="secondary" disabled={pristine || submitting} onClick={reset}>Clear Values</Button>
            </div>
        </form>
    )
};

export default reduxForm({
    form: 'addEmployeeForm',  // a unique identifier for this form
})(AddNewEmployeeForm)
