import React from "react";
import { Field, reduxForm } from "redux-form";
import FirstNameField from "../components/FirstNameField";

const AddNewEmployeeFormFunc = ({ handleSubmit }) => {

    let[firstName, setFirstName] = React.useState('');

    let formSubmit = (values) => {
        console.log(values);
    };

    return (
        <form onSubmit={ handleSubmit(formSubmit) }>
            <FirstNameField firstName={(val) => setFirstName(val)} />
            <button type="submit">Add</button>
        </form>
    );
};

const AddNewEmployeeForm = reduxForm({
   form: 'addEmployeeForm'
})(AddNewEmployeeFormFunc);

export default AddNewEmployeeForm;
