const initialState = {
    isFetching : true,
    isFetched : false,
    posts : [],
    postsError : null
};

const postsReducer = (state = initialState, action) => {
    switch(action.type) {

        case "FETCH_POSTS_INIT":
            return {
              ...state,
              isFetching: true,
              isFetched: false
            };

        case "LIST_POSTS":
            return {
                ...state,
                isFetched: true,
                isFetching: false,
                posts: action.payload
            };

        case "FETCH_POSTS_ERROR":
            return {
                isFetched: false,
                isFetching: false,
                posts: [],
                postsError: action.payload
            };

        default:
            return state
    }
};

export default postsReducer;
